con:config.script:
import com.eviware.soapui.support.XmlHolder
import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext


log.info "This is first script"
log.info context.expand('${#TestSuite#name}')
log.info context.expand('${#TestSuite#id}')

testRunner.testCase.testSuite.testCases["AddEmployee"].setPropertyValue("age","30")
log.info testRunner.testCase.testSuite.testCases["AddEmployee"].getPropertyValue("age")
log.info testRunner.testCase.testSuite.testCases["AddEmployee"].getPropertyValue("dept")

log.info testRunner.testCase.testSuite.testCases["GetDetail"].testSteps["GetInfo"].getPropertyValue("Request")

log.info testRunner.testCase.testSuite.project.getPropertyValue("Gender")

//Hittiing

def addReq=testRunner.testCase.testSuite.testCases["AddEmployee"].testSteps["AddRequest"].getPropertyValue("Request")
def name=testRunner.testCase.testSuite.testCases["AddEmployee"].getPropertyValue("name")
def id=testRunner.testCase.testSuite.testCases["AddEmployee"].getPropertyValue("id")
def age=testRunner.testCase.testSuite.testCases["AddEmployee"].getPropertyValue("age")
def dept=testRunner.testCase.testSuite.testCases["AddEmployee"].getPropertyValue("dept")
def xmlAdd = new XmlHolder(addReq)
xmlAdd.setNodeValue("//typ:addEmployee/typ:name",name)
xmlAdd.setNodeValue("//typ:addEmployee/typ:id",id)
xmlAdd.setNodeValue("//typ:addEmployee/typ:Department",dept)
xmlAdd.setNodeValue("//typ:addEmployee/typ:age",age)

def newAddXml=xmlAdd.getXml();
testRunner.testCase.testSuite.testCases["AddEmployee"].testSteps["AddRequest"].setPropertyValue("Request",newAddXml)

def addTestStep=testRunner.testCase.testSuite.testCases["AddEmployee"].testSteps["AddRequest"]
def contextAddEmp=new WsdlTestRunContext(addTestStep);
addTestStep.run(testRunner,contextAddEmp)


//Hitting getInfo
def getReq=testRunner.testCase.testSuite.testCases["GetDetail"].testSteps["GetInfo"].getPropertyValue("Request")
def xmlGet=new XmlHolder(getReq)
xmlGet.setNodeValue("//typ:getEmployeeDetails/typ:employeeName",name)
def newGetXml=xmlGet.getXml();
testRunner.testCase.testSuite.testCases["GetDetail"].testSteps["GetInfo"].setPropertyValue("Request",newGetXml)
def getTestStep=testRunner.testCase.testSuite.testCases["GetDetail"].testSteps["GetInfo"]
def contextGetInfo=new WsdlTestRunContext(getTestStep)
getTestStep.run(testRunner,contextGetInfo)


//Validation
def getRes=testRunner.testCase.testSuite.testCases["GetDetail"].testSteps["GetInfo"].getPropertyValue("Response")
def xmlGetRes=new XmlHolder(getRes)
def getResponse=xmlGetRes.getNodeValue("//ns:name")

log.info getResponse
log.info name

assert getResponse==name
