//Grrovy Script to update DA, UC for threshold notifications
//@AmitU

import com.eviware.soapui.support.XmlHolder
import com.eviware.soapui.model.iface.MessageExchange
import com.eviware.soapui.support.GroovyUtils 
import groovy.json.JsonSlurper;
import groovy.json.JsonOutput
import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext

/* 
     ****************MANDATORY INFORMATION PART****************
	1.Please provide input to every variable
	2.Provide value against DataGB if bunlde is in GB making DataMB "ZERO"
	3.Provide value against DataMB if bunlde is in MB making DataGB "ZERO"
*/

MSISDN_toUse=2349062058598
DAIDtoUpdate=213
UC=[622,5003]
Percentage=99.5
DataGB=0		
DataMB=500			

//*****************************************************


testRunner.testCase.testSuite.setPropertyValue("MSISDN",MSISDN_toUse.toString())


//Logic to check if Data in GB
if(DataGB>0){
// Converting GB TO Monetry Value
intDataGB=DataGB.toInteger()
monGB=intDataGB*1024*1024*0.0005
log.info "GB_Totoal value "+ monGB
//Calculating value of UC
valUC=((monGB*(Percentage/100))*100).toInteger()
log.info Percentage + "% of GB_UC is "+ valUC
//Calculatin value of DA
//valDA=((monGB*((100-Percentage)/100))*100).toInteger()
//log.info "GB_DA is "+ valDA

//New logic to update
valDA=(((monGB*100)-valUC)).toInteger()
log.info "GB_DA is "+ valDA

//Checking TOTAL to avoid any differnce
total= (valUC+valDA)/100
log.info "Total value is :" + total
}



//Logic if data is in MB
if(DataMB>0)
{
// Converting GB TO Monetry Value
intDataMB=DataMB.toInteger()
monMB=DataMB*1024*0.0005
log.info "MB_Totoal value "+ monMB
//Calculating value of UC
valUC=((monMB*(Percentage/100))*100).toInteger()
log.info Percentage + "% of MB_UC is "+ valUC
//Calculatin value of DA
//valDA=((monMB*((100-Percentage)/100))*100).toInteger()
//log.info "MB_DA is "+ valDA

//New logic to update
valDA=(((monMB*100)-valUC)).toInteger()
log.info "MB_DA is "+ valDA

//Checking TOTAL value to avoid any differnces
total= (valUC+valDA)/100
log.info "Total value is :" + total

}


//Convering values to String
DA_STR=DAIDtoUpdate.toString()
valDA_STR=valDA.toString()

testRunner.testCase.testSuite.setPropertyValue("DAID",DA_STR)
testRunner.testCase.testSuite.setPropertyValue("ValDAID",valDA_STR)


def updateDA=testRunner.testCase.testSuite.testCases["ThresholdChange"].testSteps["UpdateDA"]
def contextUpdateDA=new WsdlTestRunContext(updateDA)
updateDA.run(testRunner,contextUpdateDA)

log.info "DA "+ DAIDtoUpdate + " Updated Successfully"

def updateUCUT=testRunner.testCase.testSuite.testCases["ThresholdChange"].testSteps["resetUCUTMonetry"]
def contextUpdateUCUT=new WsdlTestRunContext(updateUCUT)

for(i=0;i<UC.size();i++)
{

testRunner.testCase.testSuite.setPropertyValue("MUC",UC[i].toString())
testRunner.testCase.testSuite.setPropertyValue("ValMUC",valUC.toString())

updateUCUT.run(testRunner,contextUpdateUCUT)

log.info "UC "+ UC[i] + " Updated Successfully"

}

